import React from "react";
import "./Cardpage.css";


import cp1 from '../img/cp1.jpg'
import hm3 from '../img/hm3.jpg'
import hm5 from '../img/hm5.jpg'





function Cardpage({ title, imgeUr, body, text }) {
  const arr = [
    {
    
      desc: "Ukraine war: Residents rescued after Kyiv apartment block strike",
      href:"https://www.bbc.com/news/world-europe-60733921",
      text: "Ukraine international Andriy Yarmolenko scores the opener against Aston Villa in his first West Ham appearance since the outbreak of war in his native country",
      
    },
    <div class="">
    <div class="relative">
      <img src={hm3} height="300px" width="400px" class="mb-2"/>
    </div>
   
  </div>
  ,
    {
      src: cp1,
      desc: "Ukraine crisis: US warns China against helping Russia",
      href:"https://www.bbc.com/news/world-asia-china-60732486",
    },
    {
      src: cp1,
      desc: "Poland feels the strain of Ukraine's refugees",
      href:"https://www.bbc.com/news/world-europe-60731369",
    },
   
  ];
  return (
    <>
      <div className="the_line"></div>
      <div className="continer">
        {arr.map((currElem)=>{return(
        <div className="card-container">
          <img src={currElem.src} alt="the_card_news" />
          <a href={currElem.href}>{currElem.desc}</a>
        </div>
        );})}
      </div>
    </>
  );
}

export default Cardpage;
