import React from "react";
import "./Longreads.css";
import lg from '../img/lg.jpg'
import lg1 from '../img/lg1.jpg'
import lr2 from '../img/lr2.jpg'
import lr4 from '../img/lr4.PNG'
import lr5 from '../img/lr5.jpg'




function Longreads({ title, imgeUr, body }) {
    
  const arr = [
    {
      src: lg,
      desc: "Xenotransplantation: Are pigs the future of organ transplants?",
      href:"https://www.bbc.com/news/health-60708120",
    },
    {
      
      src: lg1,
      desc: "On the front line of the battle for Kharkiv",
      href:"https://www.bbc.com/news/world-europe-60693166",
    },
    {
      src: lr2,
      desc: "Ukraine: Mother of Russian soldier asks 'Whose door'",
      href:"https://www.bbc.com/news/world-europe-60604952",
    },
    {
      src: lr4,
      desc: "Martin Compston: Plucked to stardom from a school corridor",
      href:"https://www.bbc.com/news/uk-scotland-60707536",
    },
    {
      src: lr5,
      desc: "Exempt housing: 'Living on the street is often better than this'",
      href:"https://www.bbc.com/news/uk-england-birmingham-60575193",
    },
  ];
  return (
    <>
      <div className="the_line"></div>
      <h2 class="text-slate-900 font-bold text-2xl px-20 ml-4 pb-4">Long Reades
       <hr />
    </h2>
      <div className="continer">
        {arr.map((currElem)=>{return(
        <div className="card-container">
          <img src={currElem.src} alt="the_card_news" />
          <a href={currElem.href}>{currElem.desc}</a>
        </div>
        );})}
      </div>
    </>
  );
}

export default Longreads;
