import React from "react";
import "./Card.css";

import hm1 from '../img/hm1.jpg'
import hm3 from '../img/hm3.jpg'
import hm5 from '../img/hm5.jpg'
import hm6 from '../img/hm6.jpg'




function Cardpage({ title, imgeUr, body }) {
  const arr = [
      
    {
    
      desc: "Ukraine war: Residents rescued after Kyiv apartment block strike",
      href:"https://www.bbc.com/news/world-europe-60733921",
    },
    {
      
      src: hm1,
      desc: "Can China do more to stop Russia's war in Ukraine?",
      href:"https://www.bbc.com/news/world-asia-china-60615280",
    },
    {
      src: hm3,
      desc: "How to spot false posts from Ukraine",
      href:"https://www.bbc.com/news/blogs-trending-60654288",
    },
    {
      src: hm5,
      desc: "Mass graves in Ukraine: Battered cities are digging makeshift burial sites",
      href:"https://www.bbc.com/news/world-europe-60729206",
    },
    {
      src: hm6,
      desc: "erial attacks hit city that is corridor to Kyiv",
      href:"https://www.bbc.com/news/world-europe-60729216",
    },
  ];
  return (
    <>
       <div class="">
                <div class="relative">
                  <img src={hm1} height="300px" width="400px" class="mb-2"/>
                </div>
                <div class="absoulte mt-0">
                  <h4 class="font-bold text-xl text-zinc-800 tracking-tight leading-6 mb-2">Emotional Yarmolenko scores winner</h4>
                  <p class="text-lg font-sans text-gray-600 tracking-tight leading-5 mb-2">Ukraine international Andriy Yarmolenko scores the opener against Aston Villa in his first West Ham appearance since the outbreak of war in his native country.</p>
                  <div class="bg-yellow-500 h-4 w-1 "><h4 class="uppercase text-sm ml-2"> football</h4></div>
                </div>
              </div>
      <div className="the_line"></div>
      <div className="continer">
        {arr.map((currElem)=>{return(
        <div className="card-container">
          <img src={currElem.src} alt="the_card_news" />
          <a href={currElem.href}>{currElem.desc}</a>
        </div>
        );})}
      </div>
    </>
  );
}

export default Cardpage;
